  doc.text(`PowerGym - PLAN NUTRICIONAL DE ${nombre}`, 10, 14);

  doc.setFont("helvetica", "normal");
  doc.setFontSize(12);
  doc.text(`Nombre: ${nombre}`, 10, 30);
  doc.text(`Género: ${genero}`, 10, 38);
  doc.text(`Objetivo: ${objetivo}`, 10, 46);
  doc.text(`Nivel: ${nivel}`, 10, 54);
  doc.text(`Peso: ${peso} kg`, 100, 30);
  doc.text(`Altura: ${(altura * 100).toFixed(1)} cm`, 100, 38);
  doc.text(`IMC: ${imc.toFixed(2)}`, 100, 46);
  doc.line(10, 58, 200, 58);

  // PLAN DE ALIMENTACIÓN
  doc.setFontSize(13);
  doc.setTextColor(215, 181, 0);
  doc.setFont(undefined, "bold");
  doc.text("PLAN DE ALIMENTACIÓN", 10, 70);

  // TABLA
  const tablaDieta = [];
  let ultimoTiempo = "";
  let ultimaPreparacion = "";

  plan.dieta.forEach(entry => {
    entry.ingredientes.forEach((ing, index) => {
      tablaDieta.push([
        {
          content: index === 0 && entry.tiempo !== ultimoTiempo ? entry.tiempo : "",
          styles: { fontStyle: "bold" }
        },
        {
          content: index === 0 ? entry.preparacion : (entry.preparacion !== ultimaPreparacion && index === 0 ? entry.preparacion : ""),
          styles: { fontStyle: index === 0 ? "bold" : "normal" }
        },
        ing.nombre,
        ing.medida
      ]);
    });
    ultimoTiempo = entry.tiempo;
    ultimaPreparacion = entry.preparacion;
  });

  doc.autoTable({
    head: [["TIEMPO DE COMIDA", "PREPARACIÓN", "INGREDIENTE", "MEDIDA CASERA"]],
    body: tablaDieta,
    startY: 78,
    styles: {
      halign: 'center',
      fontSize: 11.5,
      fillColor: false,
      textColor: [30, 30, 30],
      lineColor: [200, 200, 200],
      lineWidth: 0.1
    },
    headStyles: {
      fillColor: [255, 215, 0],
      textColor: [0, 0, 0]
    },
    alternateRowStyles: {
      fillColor: false
    },
    tableLineColor: [220, 220, 220],
    tableLineWidth: 0.1
  });

  let y = doc.lastAutoTable.finalY + 10;

  // RECOMENDACIONES
  doc.setFont(undefined, "bold");
  doc.setTextColor(215, 181, 0);
  doc.setFontSize(14);
  doc.text("RECOMENDACIONES NUTRICIONALES", 10, y);
  y += 8;

  doc.setFontSize(11);
  doc.setTextColor(30);

  plan.recomendaciones.forEach((r, i) => {
    const numero = `${i + 1}. `;
    const textoSinNumero = r;
    const split = doc.splitTextToSize(textoSinNumero, 172);

    if (y + split.length * 6 > 270) {
      doc.addPage();
      doc.addImage(logoData, 'PNG', 30, 50, 150, 150); // Marca de agua ✔️
      y = 20;
    }



    // Número en negrita
    doc.setFont(undefined, "bold");
    doc.text(numero, 14, y);

    // Texto normal
    doc.setFont(undefined, "normal");
    doc.text(split, 14 + doc.getTextWidth(numero), y);

    // Espacio uniforme de 8pt entre recomendaciones
    y += split.length * 6 + 2;
  });

  y += 5;


  const alturaAviso = 40;
  const alturaFirmas = 30;
  const alturaLeyenda = 10;
  const alturaTotal = alturaAviso + alturaFirmas + alturaLeyenda + 10;

  // Si no cabe, crear nueva página
  if (y + alturaTotal > 280) {
    doc.addPage();
    doc.addImage(logoData, 'PNG', 20, 50, 170, 170);
    y = 20;
  }


  doc.setFontSize(12);
  doc.setTextColor(215, 181, 0);
  doc.setFont(undefined, "bold");
  doc.text("Aviso importante!!", 14, y);
  y += 6;

  doc.setFont(undefined, "normal");
  doc.setTextColor(30);
  const aviso1 = "La dieta incluida en este documento es una recomendación general diseñada por un profesional en nutrición.";
  const aviso2 = "No debe ser adoptada por personas con condiciones médicas o tratamientos sin previa evaluación médica.";
  const aviso3 = "Si tienes alguna patología, consulta con tu médico o nutricionista antes de seguir cualquier plan alimenticio.";
  const aviso4 = "En Power Gym, tu bienestar es lo primero.";

  const split1 = doc.splitTextToSize(aviso1, 180);
  const split2 = doc.splitTextToSize(aviso2, 180);
  const split3 = doc.splitTextToSize(aviso3, 180);
  const split4 = doc.splitTextToSize(aviso4, 180);

  // Párrafo 1
  doc.text(split1, 14, y);
  y += split1.length * 6 + 2;

  // Párrafo 2 
  doc.setFont(undefined, "bold");
  doc.text(split2, 14, y);
  y += split2.length * 6 + 2;

  // Párrafo 3
  doc.setFont(undefined, "normal");
  doc.text(split3, 14, y);
  y += split3.length * 6 + 2;

  // Párrafo 4
  doc.text(split4, 14, y);
  y += split4.length * 6 + 4;


  if (y + 15 > 270) {
    doc.addPage();
    doc.addImage(logoData, 'PNG', 30, 50, 150, 150);
    y = 20;
  }

  doc.setFontSize(11);
  doc.setTextColor(0, 0, 255);
  doc.setFont(undefined, "normal");
  doc.textWithLink(
    "Descargar documento Word certificado con todas las dietas",
    14,
    y,
    { url: "https://drive.google.com/file/d/1RheQDcweoU9ASbu2HhEZ-w9P7sTef9MN/view?usp=drive_link" }
  );
  y += 12;


  // Firmas
  if (y + 35 > 270) {
    doc.addPage();
    doc.addImage(logoData, 'PNG', 30, 50, 150, 150);
    y = 20;
  }

  doc.setFontSize(12);
  doc.setTextColor(30);
  doc.setFont(undefined, "normal");
  doc.text("Dieta Certificada Por", 25, y);
  doc.text("Dieta Certificada Por", 125, y);

  doc.addImage(firmaNutri1, "PNG", 20, y + 2, 60, 30);
  doc.addImage(firmaNutri2, "PNG", 120, y + 2, 60, 29);
  y += 35;



  doc.setFontSize(10);
  doc.setTextColor(150);
  doc.text("Generado por JuanGoyeneche - © 2025", 10, 285);





  doc.save(`Dieta_PowerGym_${nombre.replace(" ", "_")}.pdf`);
